package org.tnsif.operator;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=12;
		int y=7;
		x-=y;
		System.out.println(x);
		x+=y;
		System.out.println(x);
		x*=y;
		System.out.println(x);
		x/=y;
		System.out.println(x);
		x%=y;
		System.out.println(x);
		x=y;
		System.out.println(x);
				

	}

}
